mapper.select:
SELECT id,tag_name,create_time,update_time
FROM tb_tag
WHERE tag_name = #{tagName}

SELECT id,tag_name,create_time,update_time
FROM tb_tag
WHERE id = #{id}

mapper.update:
UPDATE tb_tag
SET tag_name = #{tag.tagName},update_time = #{tag.updateTime}
WHERE id = #{tag.id}

mapper.delete:
DELETE FROM tb_tag
WHERE id IN

    #{tagId}
